function menuAction(menuActive) {
    menuActive ? menuActive = false : menuActive = true;
    $(".menu-button").toggleClass("menu-button_active")
    $(".logo").toggleClass("logo_active"),
    $(".navigation").toggleClass("navigation_active"),
    $(".sup-title").toggleClass("sup-title_active"),
    $(".sub-title").toggleClass("sub-title_active")
    return menuActive;
}

function preloader() {
    setTimeout(function() {
        var t = document.getElementById("preloader");
        t.classList.contains("preloader_disable") || t.classList.add("preloader_disable")
    }, 1e3)
}
document.body.onload = function() {
    let menuActive = false;
    const menuButton = $(".menu-button");
    const menuNavigation = $(".navigation");

    // menuButton.on("click", function() {
    //     if(!menuActive) {
    //         menuActive = menuAction(menuActive);
    //     }
    // });

    $(document).mouseup(function (e){
        if ((!menuNavigation.is(e.target) && menuNavigation.has(e.target).length === 0 && menuActive) || (menuButton.is(e.target) && !menuActive)) {
            menuActive = menuAction(menuActive);
        }
    });

    preloader()
};
